mapper.select:
select count(*)
from users
where userid like #{userid};

select *
from users
where userid like #{userid}
and passwd like #{passwd}

mapper.insert:
insert into users (userid, passwd, username, nickname, birth, tel) values (
    #{userid}, #{passwd}, #{username}, #{nickname}, #{birth}, #{tel}
)

mapper.delete:
delete from users
where userid like #{userid}
and passwd like #{passwd};

mapper.select:
select userid 
from users
where username like #{username}
and tel like #{tel}

select passwd 
from users
where userid like #{userid}
and tel like #{tel}
